mapper.select:
select fname
from fileInfo
where fno = #{fno}

mapper.update:
update myjob
   set myjob_title = #{myjob_title},
	   myjob_content = #{myjob_content},
	   myjob_upt = sysdate,
	   mname = #{mname}
 where myjob_list_num = #{myjob_list_num}

update myjob
	set readcnt = readcnt+1
where myjob_list_num = #{myjob_list_num}
